import React, { useState } from 'react';
import axios from 'axios';

function App() {
  const [file, setFile] = useState(null);
  const [response, setResponse] = useState(null);
  const [loading, setLoading] = useState(false); // New loading state

  const handleFileChange = (e) => {
    setFile(e.target.files[0]);
  };

  const handleUpload = async () => {
    if (!file) {
      alert('Please select a file first!');
      return;
    }

    const formData = new FormData();
    formData.append('bill', file);

    try {
      setLoading(true); // Start loading
      const res = await axios.post('http://localhost:3001/upload-bill', formData, {
        headers: {
          'Content-Type': 'multipart/form-data',
        },
      });
      setResponse(res.data); // Set the backend response
    } catch (error) {
      console.error('Error uploading bill:', error);
    } finally {
      setLoading(false); // Stop loading
    }
  };

  return (
    <div>
      <h1>Bill Splitter App</h1>
      <input type="file" onChange={handleFileChange} />
      <button onClick={handleUpload}>Upload Bill</button>

      {loading && <p>Loading...</p>} {/* Show loading while waiting for backend */}

      {response && (
        <div>
          <h2>Items and Prices</h2>
          {response.items.map((item, index) => (
            <div key={index}>
              <span>{item.name}: ${item.price.toFixed(2)}</span>
            </div>
          ))}
        </div>
      )}
    </div>
  );
}

export default App;
