import React, { useState } from 'react';
import axios from 'axios';

function App() {
  const [file, setFile] = useState(null);  // Store uploaded file
  const [response, setResponse] = useState(null);  // Backend response (bill details)
  const [loading, setLoading] = useState(false);  // Track loading state
  const [people, setPeople] = useState([]);  // Track people splitting the bill
  const [newPerson, setNewPerson] = useState("");  // Track new person input

  // Handle file upload
  const handleFileChange = (e) => {
    setFile(e.target.files[0]);
  };

  // Handle adding a new person
  const handleAddPerson = () => {
    if (newPerson && !people.includes(newPerson)) {
      setPeople([...people, newPerson]);
    }
    setNewPerson("");
  };

  // Handle bill upload and fetch backend response
  const handleUpload = async () => {
    if (!file) {
      alert('Please select a file first!');
      return;
    }

    const formData = new FormData();
    formData.append('bill', file);

    try {
      setLoading(true);
      const res = await axios.post('http://localhost:3001/upload-bill', formData, {
        headers: {
          'Content-Type': 'multipart/form-data',
        },
      });
      setResponse(res.data.items);  // Store backend response
    } catch (error) {
      console.error('Error uploading bill:', error);
    } finally {
      setLoading(false);
    }
  };

  return (
    <div>
      <h1>Bill Splitter App</h1>

      {/* Section for uploading the bill */}
      <div>
        <h2>Upload Bill</h2>
        <input type="file" onChange={handleFileChange} />
        <button onClick={handleUpload} disabled={loading || !file}>
          {loading ? 'Uploading...' : 'Upload Bill'}
        </button>
      </div>

      {/* Section for adding people */}
      <div>
        <h2>Add People</h2>
        <input
          type="text"
          value={newPerson}
          onChange={(e) => setNewPerson(e.target.value)}
          placeholder="Enter person name"
        />
        <button onClick={handleAddPerson}>Add Person</button>

        {/* List of people */}
        {people.length > 0 && (
          <ul>
            {people.map((person, index) => (
              <li key={index}>{person}</li>
            ))}
          </ul>
        )}
      </div>

      {/* "Go" button */}
      {file && people.length > 0 && (
        <button onClick={() => console.log('Ready to split the bill!')}>
          Go
        </button>
      )}

      {/* Display the bill items (once uploaded) */}
      {response && (
        <div>
          <h2>Items and Prices</h2>
          <table border="1">
            <thead>
              <tr>
                <th>Item</th>
                <th>Price</th>
                <th>Discount</th>
                <th>Price You Pay</th>
              </tr>
            </thead>
            <tbody>
              {/* Display each item in a row */}
              {response.split('\n').map((line, index) => (
                <tr key={index}>
                  <td>{line}</td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>
      )}
    </div>
  );
}

export default App;
